import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
} from "react-router-dom";
import Header from "./header";
import Body from "./body";
import Footer from "./footer";
import FullNote from "./fullNote";
import Login from "./login";
import Register from "./register";

function App() {
  var [passAlert, setAlert] = useState("");
  var [emailAlert, setAlert1] = useState("");
  const [isView, setView] = useState(false);
  const [islogged, setLogged] = useState(false);
  const [logText, setText] = useState("Login");
  var [noteContents, setContents] = useState([]);
  var [viewNote, setViewNote] = useState({
    title: "",
    content: "",
  });

  const MainAppLayout = () => {
    return (
      <>
        <Header></Header>
        {isView ? (
          <FullNote
            head1={viewNote.title}
            body1={viewNote.content}
            chNote={closeNote}
          ></FullNote>
        ) : null}
        <Body shNote={showNote} nC={noteContents} sC={setContents}></Body>
        <Footer></Footer>
      </>
    );
  };

  function showNote(id) {
    var blur = document.getElementById("blur");
    setView(true);
    blur.classList.add("vis");
    setViewNote(() => {
      return {
        title: noteContents[id].title,
        content: noteContents[id].content,
      };
    });
    console.log(id);
  }
  function closeNote(id) {
    var blur = document.getElementById("blur");
    setView(!isView);
    blur.classList.remove("vis");
  }

  const ProtectedRoute = ({ children }) => {
    console.log(islogged);
    const navigate = useNavigate();
    useEffect(() => {
      if (!islogged) {
        navigate("/", { replace: true });
      }
    });
    return islogged ? children : null;
  };

  return (
    <Router>
      <div className="main">
        <Routes>
          <Route
            path="/"
            element={
              <Login
                paA={passAlert}
                sA={setAlert}
                emA={emailAlert}
                sA1={setAlert1}
                text={logText}
                sText={setText}
                Login={setLogged}
              ></Login>
            }
          />

          <Route path="/register" element={<Register />} />

          <Route
            path="/keeper"
            element={
              <ProtectedRoute islogged={islogged}>
                <MainAppLayout />
              </ProtectedRoute>
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
