import React, { useState } from "react";
import notes from "../notes";
import Note from "./note";
import InputField from "./input";
import axios from "axios";

function Body(props) {
  var [inputText, setInputText] = useState({
    user_id: props.uId,
    title: "",
    content: "",
  });
  var [errorText, setError] = useState("");

  function handle(event) {
    const { name, value } = event.target;
    setInputText((prevValue) => {
      return {
        ...prevValue,
        [name]: value,
      };
    });
  }

  async function post() {
    try {
      const response = await axios.post("http://localhost:5000/api/postnote", {
        user_id: inputText.user_id.trim(),
        title: inputText.title.trim(),
        content: inputText.content.trim(),
      });
      const resData = response.data;
      props.sC((prevValue) => {
        return [
          ...prevValue,
          {
            title: resData.title,
            content: resData.content,
            created_at: resData.created_at,
          },
        ];
      });
      setInputText((prevValue) => {
        return {
          ...prevValue,
          title: "",
          content: "",
        };
      });
    } catch (error) {
      if (error.response.status === 404) {
        setError(`${error.response.data.message}`);
      }
      console.error("The post was unsuccessful:", error);
    }
  }

  function deleteNote(id) {
    props.sC((prevValue) => {
      return prevValue.filter((note, index) => {
        return index !== id;
      });
    });
  }

  return (
    <div id="blur" className="body">
      <span>{errorText}</span>
      <InputField
        tValue={inputText.title}
        teValue={inputText.content}
        onPost={post}
        onInput={handle}
      ></InputField>
      <div className="arr-notes">
        {props.nC.map((not, index) => {
          return (
            <Note
              sN={props.shNote}
              key={index}
              id={index}
              title={not.title}
              contents={not.content}
              created_at={not.created_at}
              onDelete={deleteNote}
            ></Note>
          );
        })}
      </div>
    </div>
  );
}

export default Body;
