import React, { useState } from "react";
import EditIcon from "@mui/icons-material/Edit";
import PostAddIcon from "@mui/icons-material/PostAdd";
import Zoom from "@mui/material/Zoom";

function FullNote(props) {
  const [isEditable, setEditable] = useState(false);

  function closeNote() {
    props.chNote(false);
    setEditable(false);
  }

  function edit() {
    setEditable(true);
  }

  function postEdit() {
    setEditable(false);
  }

  return (
    <div className="view">
      <div className="view-p">
        <h2 contentEditable={isEditable ? "true" : "false"} className="view-h2">
          {props.head1}
        </h2>
        <p contentEditable={isEditable ? "true" : "false"} className="vie">
          {props.body1}
        </p>
      </div>
      <div className="bnts">
        <button onClick={closeNote} className="view-close" type="button">
          close
        </button>
        {props.isV ? (
          <Zoom>
            {isEditable ? (
              <button onClick={postEdit} className="edit" type="button">
                <PostAddIcon></PostAddIcon>
              </button>
            ) : (
              <button onClick={edit} className="edit" type="button">
                <EditIcon></EditIcon>
              </button>
            )}
          </Zoom>
        ) : null}
      </div>
    </div>
  );
}

export default FullNote;
