import React, { useState, useEffect, useCallback } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
} from "react-router-dom";
import Header from "./header";
import Body from "./body";
import Footer from "./footer";
import Login from "./login";
import Register from "./register";
import axios from "axios";

function App() {
  var [passAlert, setAlert] = useState("");
  var [emailAlert, setAlert1] = useState("");
  const [islogged, setLogged] = useState(false);
  const [logText, setText] = useState("Login");
  const [userId, setUserId] = useState();
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/api/check-auth",
          { withCredentials: true }
        );
        if (response.data.is_logged) {
          setLogged(true);
          setUserId(response.data.userId); // Set uId from the check-auth response
          console.log("Frontend: User is already logged in.");
        } else {
          setLogged(false);
          setUserId(null);
          console.log("Frontend: User is not logged in.");
          navigate("/");
        }
      } catch (error) {
        console.error("Frontend: Error checking auth status:", error);
        setLogged(false); // Assume not logged in on error
        // setUid(null);
        // navigate('/'); // Force to login page on initial auth check failure
      }
    };

    checkAuthStatus();
  }, []);

  const MainAppLayout = () => {
    return (
      <>
        <Header setTogg={setToggle}></Header>
        <Body setTog={setToggle} tog={toggle} uId={userId}></Body>
        <Footer></Footer>
      </>
    );
  };

  // const ProtectedRoute = ({ children }) => {
  //   console.log(islogged);
  //   const navigate = useNavigate();
  //   useEffect(() => {
  //     if (!islogged) {
  //       navigate("/", { replace: true });
  //     }
  //   });
  //   return islogged ? children : null;
  // };

  return (
    <Router>
      <div className="main">
        <Routes>
          <Route
            path="/"
            element={
              <Login
                sUserId={setUserId}
                paA={passAlert}
                sA={setAlert}
                emA={emailAlert}
                sA1={setAlert1}
                text={logText}
                sText={setText}
                setLog={setLogged}
              ></Login>
            }
          />

          <Route path="/register" element={<Register />} />

          <Route path="/keeper" element={islogged ? <MainAppLayout /> : null} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
