import React, { useState, useEffect, useCallback } from "react";
import Note from "./note";
import InputField from "./input";
import axios from "axios";
import FullNote from "./fullNote";
import { useSpring, animated } from "@react-spring/web";

function Body(props) {
  var [inputText, setInputText] = useState({
    user_id: props.uId,
    title: "",
    content: "",
  });
  var [errorText, setError] = useState("");
  var [noteContents, setContents] = useState([]);
  const [isView, setView] = useState(false);
  var [viewNote, setViewNote] = useState({
    title: "",
    content: "",
  });

  const springProps = useSpring({
    from: { opacity: 0, transform: "scale(0)" }, // Start fully zoomed out and invisible
    to: {
      opacity: isView ? 1 : 0,
      transform: isView ? "scale(1)" : "scale(0)",
    }, // Target state based on `isVisible`
    config: { mass: 1, tension: 300, friction: 20 }, // Customize spring physics
  });

  function handle(event) {
    const { name, value } = event.target;
    setInputText((prevValue) => {
      return {
        ...prevValue,
        [name]: value,
      };
    });
  }

  const loadNotes = useCallback(async () => {
    if (!props.uId) return;
    try {
      const response = await axios.get(
        `http://localhost:5000/api/keeper/${props.uId}`
      );
      const noteData = response.data.notes;
      console.log(noteData);
      setContents(() => {
        return [...noteData];
      });
    } catch (error) {
      console.error("Unable to get user notes:", error);
    }
  }, [props.uId]);

  useEffect(() => {
    loadNotes();
  }, [loadNotes]);

  async function post() {
    console.log(inputText.user_id, inputText.content);
    try {
      const response = await axios.post("http://localhost:5000/api/postnote", {
        user_id: inputText.user_id,
        title: inputText.title,
        content: inputText.content,
      });
      const resData = response.data;
      setContents((prevValue) => {
        return [
          ...prevValue,
          {
            title: resData.title,
            content: resData.content,
            created_at: resData.created_at,
            note_id: resData.note_id,
          },
        ];
      });
      setInputText((prevValue) => {
        return {
          ...prevValue,
          title: "",
          content: "",
        };
      });
    } catch (error) {
      if (error.response.status === 404) {
        setError(`${error.response.data.message}`);
      }
      console.error("The post was unsuccessful:", error);
    }
  }

  async function deleteNote(id, noteId) {
    setContents((prevValue) => {
      return prevValue.filter((note, index) => {
        return index !== id;
      });
    });

    try {
      console.log(noteId);
      const response = await axios.delete(
        `http://localhost:5000/api/keeper/${noteId}`
      );
    } catch (error) {
      console.error("Delete failed:", error);
    }
  }

  function showNote(id) {
    var blur = document.getElementById("blur");
    setView(true);
    blur.classList.add("vis");
    console.log(blur);
    setViewNote(() => {
      return {
        title: noteContents[id].title,
        content: noteContents[id].content,
      };
    });
    console.log(id);
  }
  function closeNote(id) {
    var blur = document.getElementById("blur");
    setView(!isView);
    blur.classList.remove("vis");
    console.log(blur);
  }

  return (
    <div className="body">
      <animated.div
        style={{
          ...springProps,
          zIndex: 9999,
          position: "fixed",
          width: "100%",
          display: "flex",
          justifyContent: "center",
        }}
      >
        <FullNote
          head1={viewNote.title}
          body1={viewNote.content}
          chNote={closeNote}
        ></FullNote>
      </animated.div>

      <div id="blur">
        <span>{errorText}</span>
        <InputField
          tValue={inputText.title}
          teValue={inputText.content}
          onPost={post}
          onInput={handle}
        ></InputField>
        <div className="arr-notes">
          {noteContents.map((not, index) => {
            return (
              <Note
                sN={showNote}
                key={not.note_id}
                id={index}
                noteId={not.note_id}
                title={not.title}
                contents={not.content}
                created_at={not.created_at}
                onDelete={deleteNote}
              ></Note>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Body;
